import './App.css';
import { useState } from 'react';
function App() {
  const [opcionSeleccionada, setOpcionSeleccionada] = useState('');
  const opcionesDisponibles = ['Opción A', 'Opción B', 'Opción C']; // Tus opciones válidas

  return (
    <div className='App'>
      <h1>Horarios FAMAF</h1>
      <select value={opcionSeleccionada} onChange={(e) => setOpcionSeleccionada(e.target.value)}>
      <option value="">-- Selecciona una --</option>
      {opcionesDisponibles.map((opcion, index) => (
        <option key={index} value={opcion}>{opcion}</option>
      ))}
    </select>
      <label for="surnameId" class="form-label">Carrera: </label>
      <input type="text" minlength="3" class="form-control" list="carrerasDisponibles" id="surnameId" required></input>
      <datalist id="carrerasDisponibles">
        <option value="Computacion"></option>
        <option value="mate aplicada"></option>
        <option value="fisica"></option>
        <option value="astronomia"></option>
        <option value="hidrometeorologia"></option>
        <option value="matematica"></option>
      </datalist>
      <label for="surnameId" class="form-label">Año de cursada: </label>
      <input type="text" minlength="3" class="form-control" id="surnameId" required></input>
      <label for="surnameId" class="form-label">Cuatrimestre: </label>
      <input type="text" minlength="3" class="form-control" id="surnameId" required></input>
    </div>
  );
}

export default App;
