import './App.css';
import { useState } from 'react';

function optMap({items}){
  return items.map((option, index) => (
    <option key={index} value={option}>{option}</option>
  ));
}
function App() {
  const [carrera, setCarrera] = useState('');
  const [Anio, setAnio] = useState('');
